mapper.insert:
update department
set name = #{name}

    , enterprise_id = #{enterpriseId}


    , manager_id = #{managerId}


    , parent_id = #{parentId}


    , level = #{level}

, and operator_id = #{operatorId}
,update_time = now()
where id=#{id}

mapper.select:
select d.id, d.enterprise_id, d.name, d.parent_id, d.manager_id,up1.sgname as managerName, p.name as parentName,du.id as departmentUserId, du.department_id, up2.user_id, up2.sgname as userSgname from department d left join department p on d.parent_id = p.id left join user_profile up1 on d.manager_id=up1.user_id left join department_user on du.department_id=d.id left join user_profile up2 on du.user_id=up2.user_id where delete_flg=0
and d.name like concat('%',#{name},'%')
and d.enterprise_id=#{enterpriseId}
and d.level=#{level}
order by d.id desc

select count(*) from department d
where delete_flg=0

    and d.name like concat('%',#{name},'%')


    and d.enterprise_id=#{enterpriseId}


    and d.level=#{level}

select d.*, p.name as parentName, p1.sgname as managerName
from department d left join department p on d.parent_id = p.id left join user_profile p1 on d.manager_id=p1.user_id
where id=#{id}
