mapper.insert:
update department
set name = #{name}

    , enterprise_id = #{enterpriseId}


    , manager_id = #{managerId}


    , parent_id = #{parentId}


    , level = #{level}

, and operator_id = #{operatorId}
,update_time = now()
where id=#{id}

mapper.select:

select d.*, p.name as parentName from department d left join department p on d.parent_id = p.id
where delete_flg=0

    and d.name like concat('%',#{name},'%')


    and d.enterprise_id=#{enterpriseId}


    and d.level=#{level}

order by d.id desc

select count(*) from department d
where delete_flg=0

    and d.name like concat('%',#{name},'%')


    and d.enterprise_id=#{enterpriseId}


    and d.level=#{level}
