mapper.select:
select eu.id, eu.enterprise_id as enterpriseId, eu.user_id as userId,eu.create_time as createTime, eu.update_time as updateTime, p.sgname as userSgname, d.id as departmentId, d.name as departmentName, d.parent_id as parentId, d.manager_id as managerId from enterprise_user eu left join user_profile p on eu.user_id=p.user_id left join department d on d.enterprise_id=eu.enterprise_id where 1=1
and eu.enterprise_id=#{enterpriseId}
and p.sgname like concat('%',#{sgname},'%')
and d.id=#{departmentId}
order by d.id desc

select count(*)
from enterprise_user eu left join user_profile p on eu.user_id=p.user_id left join department d on d.enterprise_id=eu.enterprise_id
where 1=1

    and eu.enterprise_id=#{enterpriseId}



    and p.sgname like concat('%',#{sgname},'%')


    and d.id=#{departmentId}

select eu.*, p.sgname as userSgname, d.id as departmentId, d.name as departmentName
from enterprise_user eu left join user_profile p on eu.user_id=p.user_id left join department d on d.enterprise_id=eu.enterprise_id
where eu.id=#{id}
