mapper.select:

select c.* from project_class c left join project_module m on c.module_id = m.id
where delete_flg=0

    and module_id = #{moduleId}


    and m.block_id = #{blockId}

order by name

select count(*) from project_class c left join project_module m on c.module_id = m.id
where delete_flg=0

    and module_id = #{moduleId}


    and m.block_id = #{blockId}

select id, name from project_class c left join project_module m on c.module_id = m.id
where delete_flg=0

    and module_id = #{moduleId}


    and m.block_id = #{blockId}

order by name

select * from project_class where delete_flg=0

    and module_id = #{moduleId}


    and m.block_id = #{blockId}

order by name
