mapper.insert:
update scene set id=#{id}

    ,name = #{name}


    ,detail = #{detail}


    ,image_id = #{imageId}


    ,location_y = #{locationY}


    ,location_x = #{locationX}


    ,scene_type_id = #{sceneTypeId}

where id=#{id}

mapper.select:

select s.*,si.path as imageUrl

, ps.seq

from scene s left join block_image si on s.image_id=si.id

left join project_scene ps on s.id=ps.scene_id

where s.delete_flg=0

    and ps.project_id = #{projectId}


    and s.block_id = #{blockId}


    and s.scene_type_id = #{sceneTypeId}


    and s.name = #{name}


order by seq

select count(*) from scene s

left join project_scene ps on s.id=ps.scene_id

where s.delete_flg=0

    and ps.project_id = #{projectId}


    and s.scene_type_id = #{sceneTypeId}


    and s.block_id = #{blockId}


    and s.name = #{name}

select distinct(s.id), s.name, s.scene_type_id as sceneTypeId, s.code from scene s
left join project_scene ps on ps.scene_id = s.id
left join project p on ps.project_id=p.id
where s.delete_flg=0

    and s.block_id = #{blockId}


    and ps.project_id = #{projectId}


    and s.scene_type_id = #{sceneTypeId}


    and p.module_id = #{moduleId}


    and p.class_id = #{classId}

order by s.name

select s.*,si.path as imageUrl

    , ps.seq

from scene s left join block_image si on s.image_id=si.id

    left join project_scene ps on s.id=ps.scene_id

where s.delete_flg=0

    and ps.project_id = #{projectId}


    and s.scene_type_id = #{sceneTypeId}


    and s.block_id = #{blockId}


    and s.name = #{name}


order by seq
